a {
  &:hover {
    h3 {
      color: #fea162;
      text-decoration: none;
    }

    text-decoration: none;

    svg path {
      fill: #fea162;
    }
  }

  color: #fff;
  text-decoration: none;
}

.uk-button:hover {
  color: #fea162;
}

.swipe {
  z-index: 999;
  position: absolute;
  width: 60px;
  left: 45%;
  top: 30%;

  @media (max-width: 768px) {
    left: 35%;
  }
}

a-scene img.visible {
  display: block;
}

.hand-icon {
  position: relative;
  background-image: url(https://i.postimg.cc/8556gm60/hand.png);
  background-repeat: no-repeat;
  background-position: center;
  width: 100px;
  height: 100px;
  animation: spin 2s infinite;
  transform-origin: 50% 50%;

  @keyframes spin {
    0% {
      transform: rotate(30deg) translateX(20px);
    }
    50% {
      transform: rotate(-15deg) translateX(-20px);
    }
    100% {
      transform: rotate(0deg) translateX(20px);
    }
  }
}

.menu-toggle {
  position: absolute;
  top: 30px;
  right: 30px;
  z-index: 9999;
  cursor: pointer;
  color: #000;
  font-size: 35px;
}

.menu {
  display: none;
  position: absolute;
  top: 90px;
  right: 50px;
  background-color: whitesmoke;
  padding: 20px;
  border-radius: 5px;
  z-index: 9998;
  width: 150px;

  a {
    display: block;
    color: black;
    text-decoration: none;
    margin-bottom: 10px;
    font-size: 18px;
  }
}

@media only screen and (max-width: 600px) {
